// Function to count the occurrences of a word in a file
int countWordOccurrences(const std::string& filename, const std::string& word) {
    std::ifstream file(filename);
    if (!file.is_open()) {
        std::cerr << "Unable to open file " << filename << std::endl;
        return -1;
    }

    int occurrences = 0;
    std::string line;
    while (std::getline(file, line)) {
        // Convert the line to lowercase and remove punctuation
        std::string cleanedLine = line;
        std::transform(cleanedLine.begin(), cleanedLine.end(), cleanedLine.begin(),
            [](unsigned char c){ return std::tolower(c); });
        cleanedLine.erase(std::remove_if(cleanedLine.begin(), cleanedLine.end(),
            [](unsigned char c){ return !std::isalnum(c) && c != ' '; }), cleanedLine.end());

        // Count occurrences of the word in the cleaned line
        size_t pos = cleanedLine.find(word);
        while (pos != std::string::npos) {
            occurrences++;
            pos = cleanedLine.find(word, pos + 1);
        }
    }
    file.close();
    return occurrences;
}

int main() {
    std::string filename = "example.txt"; // Replace with your file name
    std::string word = "example"; // Replace with your word

    int occurrences = countWordOccurrences(filename, word);
    if (occurrences != -1) {
        std::cout << "The word '" << word << "' occurs " << occurrences << " times in the file." << std::endl;
    }
    return 0;
}
