/**
 * Checks if any of the numbers in the input array are prime numbers.
 *
 * @param {number[]} numbers - An array of 5 numbers.
 * @returns {void}
 */
function checkForPrimeNumbers(numbers) {
  if (numbers.length !== 5) {
    throw new Error('Input array must contain exactly 5 numbers');
  }

  for (const number of numbers) {
    if (number <= 1) {
      continue;
    }

    if (isPrime(number)) {
      console.log(`Found a prime number: ${number}`);
    }
  }
}

/**
 * Checks if a number is prime.
 *
 * @param {number} num - The number to check.
 * @returns {boolean} True if the number is prime, false otherwise.
 */
function isPrime(num) {
  if (num <= 1) {
    return false;
  }

  for (let i = 2; i <= Math.sqrt(num); i++) {
    if (num % i === 0) {
      return false;
    }
  }

  return true;
}
```

