#include <iostream>
#include <fstream>
#include <sstream>
#include <string>
#include <map>

/**
 * Function to count the occurrences of a word in a file.
 *
 * @param filename  The name of the file to read.
 * @param word     The word to search for.
 * @return The number of occurrences of the word.
 */
int countWordOccurrences(const std::string& filename, const std::string& word) {
  std::ifstream file(filename);
  if (!file.is_open()) {
    std::cerr << "Error: Unable to open file " << filename << std::endl;
    return -1;
  }

  std::map<std::string, int> wordCount;
  std::string line;

  while (std::getline(file, line)) {
    std::istringstream iss(line);
    std::string token;
    while (iss >> token) {
      // Convert to lowercase for case-insensitive search
      for (char& c : token) {
        c = std::tolower(c);
      }
      if (token == word) {
        ++wordCount[token];
      }
    }
  }

  file.close();
  return wordCount[word];
}

int main() {
  std::string filename, word;
  std::cout << "Enter the filename: ";
  std::cin >> filename;
  std::cout << "Enter the word to search for: ";
  std::cin >> word;

  int occurrences = countWordOccurrences(filename, word);
  if (occurrences != -1) {
    std::cout << "The word '" << word << "' occurs " << occurrences << " time(s) in the file." << std::endl;
  } else {
    std::cerr << "Error: Unable to read file or word not found." << std::endl;
  }

  return 0;
}
